mapper.select:
SELECT
    ttu.`status` AS taskUserStatus,
    tui.nick_name AS nickName,
    tui.head_img AS headImg,
    tui.id AS taskUserId,
    tt.*
FROM
    ta_task_user ttu
    INNER JOIN ta_task tt ON ttu.task_id = tt.id
    INNER JOIN tj_user_info tui ON tui.uid = tt.uid
WHERE
    ttu.uid = #{uid}

        AND ttu.`status` = #{status}


        AND tt.type = #{type}


        AND ttu.create_time > #{startTime}


        AND ttu.create_time < #{endTime}

    ORDER BY ttu.create_time DESC
    LIMIT #{pagenum}, #{pagesize}

SELECT
    tui.nick_name AS nickName,
    tui.head_img AS headImg,
    tt.*
FROM
    ta_task tt
    INNER JOIN tj_user_info tui ON tui.uid = tt.uid
WHERE
    tt.uid = 1

        AND tt.`state` = #{status}


        AND tt.type = #{type}


        AND tt.start_time > #{startTime}


        AND tt.start_time < #{endTime}

    ORDER BY tt.create_time DESC
    LIMIT #{pagenum}, #{pagesize}
